package com.example.nathaniels.workoutappv01;

import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.util.Log;

import java.util.ArrayList;

public class IngredientList extends AppCompatActivity {

    private static final String TAG = "IngredientList";

    private ArrayList<String> mNames = new ArrayList<>();
    private ArrayList<String> mImageUrls = new ArrayList<>();

    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_recipe_main_page);
        Log.d(TAG, "IngredientList onCreate: started");

        //Call the function that will populate the list of recipe ingredients
        initIngredientList();

    }

    private void initIngredientList() {
        mImageUrls.add("data:image/jpeg;base64,");
        mNames.add("Noodles");

        mImageUrls.add("data:image/jpeg;base64,");
        mNames.add("Broth");

        initRecyclerView();
    }

    private void initRecyclerView() {
        Log.d(TAG, "initRecyclerView: init recyclerview for IngredientList");
        RecyclerView recyclerView = findViewById(R.id.recycler_view);
        IngredientRecyclerViewAdapter adapter = new IngredientRecyclerViewAdapter(this, mNames, mImageUrls);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }
}
